

public class AddNoOneToTen {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		int sum=0;
		
		
		for(int i=0;i<=a;i++) {
			sum=sum+i;
			System.out.println("Addition=`"+sum);
		}
		
		//System.out.println("Addition=`"+sum);
		
		
		
		
		
		
	}

}
