
public class Incr_Decr_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		System.out.println(a--);    //10........>9                         //post decr
		System.out.println(--a);   //8                                     //pre decr
		System.out.println(a++);   //8...........>9                        //post incr
		System.out.println(++a);   //10                                    //pre incr
	}

}
